mapper.select:

where a.del_flag = '0' and a.recharge_id in

        #{id}

order by a.create_time desc



     and a.recharge_id = #{rechargeId}
     and a.oil_card_id = #{oilCardId}
     and b.card_type = #{cardType}
     and b.open_card_place = #{openCardPlace}
     and b.oil_card_number = #{oilCardNumber}
     and c.driver_name = #{driverName}
     and t.attribute = #{attribute}


     and a.major_id = #{majorId}

    and a.truck_id in

            #{truckId}





        and date_format(a.apply_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(a.apply_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')

     and a.recharge_sum = #{rechargeSum}
     and a.apply_remark = #{applyRemark}
     and a.recharge_type = #{rechargeType}
     and a.owner_driver_id = #{ownerDriverId}

     and a.order_id = #{orderId}
     and a.is_passed = #{isPassed}
     and a.fail_remark = #{failRemark}
     and date_format(a.recharge_date,'%Y-%m-%d') = #{rechargeTime}
     and a.month_order_num = #{monthOrderNum}
     and a.month_recharge_sum = #{monthRechargeSum}
     and a.create_by = #{createBy}
     and a.create_time = #{createTime}
     and a.update_by = #{updateBy}
     and a.update_time = #{updateTime}
      and a.tenant_id = #{tenantId}
     and d.major_number = #{majorNumber}
    and a.del_flag = '0'

order by a.create_time desc
